function WIYN() {
    Swal.fire({
        icon: 'warning',
        title: "Bạn cần đăng nhập để tiếp tục!",
        showCancelButton: true,
        confirmButtonColor: '#4a90e2',
        cancelButtonColor: '',
        confirmButtonText: 'Đăng nhập'
    }).then((result) => {
        if (result.value) {
            $('#exampleModal1').modal('show');
        }
    })
}
function AwToast(icon, content, timer) {
    Swal.fire({
        icon: icon,
        title: content,
        timer: timer
    })
}
