$(window).on('load', function () {
    const mgs_type = $('input[name=u_mgs_type]').val();
    const msg = $('input[name=u_mgs]').val();
    if (mgs_type != '' && msg != '') {
        switch (mgs_type) {
            case 'success':
                AwToast(mgs_type, msg, 10000)
                break;
            case 'warning':
                AwToast(mgs_type, msg, 10000)
                break;
            case 'danger':
                AwToast(mgs_type, msg, 10000)
                break;
        }
    }
});

