$(function () {
    $('#buy-btn').off('click').on('click', function (e) {
        e.preventDefault();
        var count = $('input[name="count-xyz"]').val();
        if ($('.etd').val() >= count) {
            alert("Số lượng vượt quá số lượng cho phép. Vui lòng mua với số lượng ít hơn số lượng có sẵn!")
        }
        else {
            $.ajax({
                url: 'cart/add',
                type: 'POST',
                data: {
                    pid: $(this).data('id'),
                    qty: $('.etd').val()
                },
                dataType: 'json',
                success: function (response) {
                    if (response.result == 1) {
                        $('.slc').text(parseInt($('.slc').text()) + 1);
                        $('.list-cart').load('module/mitc');
                        AwToast('success', 'Đã thêm sản phẩm vào giỏ hàng');

                    } else if (response.result == 2) {
                        $('.list-cart').load('module/mitc');
                        AwToast('success', 'Đã cập nhật lại số lượng sản phẩm trong giỏ hàng');
                    }
                }
            })
        }
    })
})
