$(function () {
    $('.update').off('click').on('click', function (e) {
        e.preventDefault();

        $.ajax('cart/update', {

            type: 'POST',
            data: { pid: $(this).data('id'), option: $(this).data('options') },
            dataType: 'json',
            success: function (res) {
                if (res == 1 || res == 2) {
                    $('.list-cart').load('module/mitc');
                    $('#checkout').load('module/icart');
                    return;
                } else if (res == 3) {
                    $('.list-cart').load('module/mitc');
                    $('#checkout').load('module/icart');
                    AwToast('info', 'Đã xóa sản phẩm khỏi giỏ hàng!', 5000);
                    return;
                } else if (res == 11) {
                    $('.list-cart').load('module/mitc');
                    $('#checkout').load('module/icart');
                    alert("Số lượng không được nhỏ hơn 1!")
                    return;
                }
                else {
                    $('.list-cart').load('module/mitc');
                    $('#checkout').load('module/icart');
                    alert("Sản phẩm vượt quá số lượng cho phép!")
                }
                AwToast('danger', '404!', 5000);
            }
        })
    })
})


//mouse - click


$(document).ready(function () {
    $('#checkout').click(function (e) {
        e.preventDefault();
        if ($('#email').val() == null || $('#email').val() == '') {
           /* Swal.fire('Vui lòng nhập email!');*/
            return false;
        } else if (($('#address').val().trim() == null || $('#address').val().trim() == '')) {
            Swal.fire('Vui lòng nhập địa chỉ nhân hàng!');
            return false;
        }
        $.ajax({
            url: "/cart/checkauth",
            type: 'POST',
            success: function (f) {
                if (f.f == 0) {
                    WIYN();
                } else {
                    $.ajax({
                        url: "/cart/payment",
                        type: 'POST',
                        data: {
                            Email: $('input[name=EmailOrder]').val(),
                            Phone: $('input[name=Phone]').val(),
                            Address: $('input[name=AddressOrder]').val(),
                            FullName: $('input[name=FullName]').val(),
                            CodeVoucher: $('input[name=Voucher]').val(),
                        },
                        dataType: 'json',
                        success: function (pig) {
                            if (!pig) {
                                window.location.href = "cart/checkout";
                            } else {
                                window.location.href = "account/order";
                            }
                        }
                    })
                }
            }
        })
    })
});


